import numpy as np

def make_diagonal(x : list[int | float]) -> list[list[int | float]]:
    l = [[0] * len(x) for _ in range(len(x))]
    for i in range(len(x)):
        l[i][i] = x[i]
    return np.array(l).tolist()

x = np.array([1, 2, 3])
output = make_diagonal(x)
print(output)
